    @PostConstruct
    @Transactional 
    public void initTheaters() {
        if (theaterRepository.count() == 0) { // DBì— ê·¹ì¥ ë°ì´í„°ê°€ ì—†ì„ ê²½ìš°ì—ë§Œ ì‹¤í–‰
            log.info("Initializing dummy theater data...");
            EXAMPLE_THEATER_NAMES_BY_REGION.forEach((region, theaterInfos) -> {
                theaterInfos.forEach(info -> {
                    theaterRepository.save(Theater.builder().name(info.name).region(region).address(info.address).subway(info.subway).bus(info.bus).parkGuide(info.parkGuide).parkCheck(info.parkCheck).parkFee(info.parkFee).build());
                });
            });
            log.info("Dummy theater data initialization complete.");
        }
    }

    public List<TheaterDto> getAllTheaters() {
        log.debug("Fetching all theaters");   
        List<Theater> theaters = theaterRepository.findAll();
        if (theaters.isEmpty()) {
            log.info("No theaters found");
        }
        return theaters.stream()
                .map(TheaterDto::fromEntity)
                .collect(Collectors.toList());
    }
    
    public List<TheaterDto> getTheatersByRegion(String region) {
        log.debug("Fetching theaters for region: {}", region); 
        List<Theater> theaters = theaterRepository.findByRegion(region);
        if (theaters.isEmpty()) {
            log.info("No theaters found for region: {}", region);
        }
        return theaters.stream()
                .map(TheaterDto::fromEntity)
                .collect(Collectors.toList());
    }
    
    public TheaterDto getTheaterById(Long theaterId) {
        log.debug("Fetching theater by ID: {}", theaterId);
        Theater theater = theaterRepository.findById(theaterId)
                .orElseThrow(() -> {
                    log.warn("Theater not found with ID: {}", theaterId);
                    return new ResourceNotFoundException("í•´ë‹¹ IDì˜ ê·¹ì¥ì„ ì°¾ì„ ìˆ˜ ì—†ìŠµë‹ˆë‹¤: " + theaterId);
                });
        return TheaterDto.fromEntity(theater);
    }

